<style>
    body{
        /*font-family:Arial,Helvetica,sans-serif;*/
        /*font-size: 15px;*/
        /*line-height: 1.5;*/
        font: 15px/1.5 Arial,Helvetica,sans-serif;
        padding: 0;
        margin: 0;
        background-color: #fffeec;
    }
    /*Global*/
    .container{
        width: 80%;
        margin: auto;
        overflow: hidden;
    }
    header ul{
        margin: 0;
        padding: 0;
    }
    .button_1{
        height:38px;
        background: #2e5e86;
        border: 0;
        padding-left: 20px;
        color: #fffacc;
        padding-left: 20px;
        padding-right: 20px;

    }

    header{
        background-color: #003775 ;
        color: #fffacc;
        padding-top:30px ;
        min-height: 70px;
        border-bottom: red 3px solid;
    }
    header a{
        color: #fffacc;
        text-decoration: none;
        text-transform: uppercase;
        font-size: 16px;
    }

    header li{
        float: left;
        display: inline-block;
        padding: 0 20px 0 20px;
    }
    header #brandig{
        float: left ;
    }
    header #brandig h1{
        margin: 0;
    }
    header nav{
        float: right;
        margin-top:10px ;
    }
    header .highlight, header .current a{
        color: red;
    }
    header a:hover{
      color: orangered;
        font-weight: bold;
    }

    #showcase{
        min-height:400px ;
        background-image:url('https://s3.envato.com/files/225693685/Preview_Image_00000.jpg');
        text-align: center;
        color: #fffacc;
    }

    #showcase h1{
        margin-top: 100px;
        font-size: 55px;
        margin-bottom: 10px;
    }
    #showcase p{
        font-size: 20px;
    }
    #newsletter{
        padding: 15px;
        color: #fffacc;
        background: #354;
    }
    #newsletter h1{
        float: left;
    }
    #newsletter form{
        float: right;
        margin-top: 15px;
    }
    #newsletter input[type='email']{
        padding: 4px;
        height: 25px;
        width: 250px;
    }
    /*Boxes*/
    #boxes{
        margin-top: 20px;
    }
    #boxes .box{
        float: left;
        text-align: center;
        width: 30%;
        padding: 10px;
    }
    #boxes .box img{
        width:90px;
    }

    footer{
        padding: 20px;
        margin-top: 20px;
        color: #fffacc;
        background-color: #0b51c5;
        text-align: center;
    }
    @media(max-width: 900px){
        header #brandig,
        header nav,
        header nav li,
        #newsletter h1,
        #newsletter form,
        #boxes .box
       {
            text-align: center;
            float: none;
            width: 100%;
        }
        header{
            padding-bottom:20px ;
        }
        #showcase h1{
            margin-top: 40px;
        }
        #newsletter button{
            display: block;
            width: 100%;
        }
        #newsletter form input[type="email"]{
            width:100%;
            margin-bottom: 5px;
        }
        @media(max-width: 330px){
            #brandig h1,
            #brandig{
                margin-left: 13px;
                font-size: 20px;
            }
            nav a{
                font-size: 15px;
            }
        }

    }



</style>
</html>